	setBar("Health", CurrentHP/MaxHP);
	setBarVisible("Health", 0)
]
<!-- Conditions -->
[H: TCondition = macro.args]

<!-- KnockedOut is used for unconsciousness as a result of being reduced to 0 HP. -->
<!-- The death threshold varies, based on campaign settings and being an NPC/PC. -->
[H, if(isNPC() && NPCS_DIE_AT_0_HP), CODE: {
	[H: state.Dead = if(CurrentHP <= 0, 1, state.Dead)]	
};
{ <!-- Is a PC or NPCS_DIE_AT_0_HP is false -->
	[H: state.Helpless = if(CurrentHP <= 0, 1, 0)] 
	[H, if(state.Helpless  && CurrentHP > 0): TCondition = 4]
};]

[H, if(TCondition == 5), CODE:{
	[H: TCondition = 4]
	[H: state.Helpless = 1]
	}]
[H, if(TCondition == 6), CODE:{
	[H: TCondition = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 1]
	}]

<!-- Set Condition and State -->
[H, if(!state.Dead), CODE:{
[H, switch(TCondition), CODE:
	case 0 : {
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	};
	case 1 : {
	[H: Condition = -1]
	[H: state.One = 1]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	};
	case 2 :{
	[H: Condition = -2]
	[H: state.One = 0]
	[H: state.Two = 1]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	};
	case 3 : {
	[H: Condition = -5]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 1]
	[H: state.Ten = 0]
	};
	case 4 :{
	[H: Condition = -10]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 1]
	};
	default: {
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	};
]
}]

