[H: TCondition = listInsert(TCondition, 5, "Disabled")]
[H: TCondition = listInsert(TCondition, 6, "Dead")]

[H: success = input("constring | " + TCondition + " | Select a Condition | LIST")]
[H: abort(success)]

<!-- Set Condition and State -->
[H, switch(constring), CODE:
	case 0 : {
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
	case 1 : {
	[H: Condition = -1]
	[H: state.One = 1]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
	case 2 :{
	[H: Condition = -2]
	[H: state.One = 0]
	[H: state.Two = 1]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
	case 3 : {
	[H: Condition = -5]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 1]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
	case 4 :{
	[H: Condition = -10]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 1]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
	case 5 :{
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 1]
	[H: state.Dead = 0]
	};
	case 6 :{
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 1]
	};
	default: {
	[H: Condition = 0]
	[H: state.One = 0]
	[H: state.Two = 0]
	[H: state.Five = 0]
	[H: state.Ten = 0]
	[H: state.Helpless = 0]
	[H: state.Dead = 0]
	};
]
[H, if(state.Dead ==1), CODE:{
[h:Output="Dead"]};{
[h:Output=Condition]}
;]

<b><font color='green'>Condition Changed to:</font> [r:Output]</b>

[H: BIG = getLibProperty("TOOLTIP_BIG_FONT_SIZE", "Lib:Zaro")]
[H: BIGGER = getLibProperty("TOOLTIP_BIGGER_FONT_SIZE", "Lib:Zaro")]
[H: tTripleCrit = getStrProp(Feats, "TripleCrit")]
[h:varsFromStrProp(arg(0))]

[H:critroll=0]
[H:DieRoll=1d20]

[H:EDmgDice=eval(dmgDice)]
[H:TotalHit=DieRoll+bab+usestat+atk+tempBonusHit+OHpen+Condition]
[H, if(DieRoll==20), CODE:{
[H:DieRolltwo=1d20]
[H:critchance=DieRolltwo+bab+usestat+atk+tempBonusHit+OHpen+Condition]
[H, if(tripleCrit && tTripleCrit):critmod=3;critmod=2]
[H:critroll=1]
}]
<table>
<tr bgcolor='#D6D7C6'><td><i>&nbsp;
	<span title='<html>
		<font size=[r:BIG]>[r:DieRoll]</font> (d20)
		+ <font size=[r:BIG]>[r:bab]</font> (Base Attack)
		+ <font size=[r:BIG]>[r:usestat]</font> (Ability)
		{if(atk,	" + <font size="+BIG+">" + atk + "</font> (Attack Bonus)", "")}
		{if(tempBonusHit,	" + <font size="+BIG+">" + tempBonusHit + "</font> (Temp Bonus)", "")}
		{if(OHpen,	" + <font size="+BIG+">" + OHpen + "</font> (Off-Hand Penalty)", "")}
		{if(Condition,	" + <font size="+BIG+">" + Condition + "</font> (Condition)", "")}
		= <font size=[r:BIGGER]>[r:TotalHit]</font>
		</html>'>
<b><font color=blue>[r:name]</font>:</b> Rolled <b>[r:TotalHit] ([r:Option]).</b>
</span>
</i></td>
[if(critroll==1), CODE:{
<td><i>&nbsp;
	<span title='<html>
